'use client';

import { useState, useEffect } from 'react';
import { createClient } from '@/lib/supabase-browser';

const LOGIN_BG_FALLBACK = 'data:image/jpeg;base64,';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [mode, setMode] = useState('login');
  const [resetSent, setResetSent] = useState(false);
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [bgUrl, setBgUrl] = useState('');
  const [showPass, setShowPass] = useState(false);
  const [confirmPassword, setConfirmPassword] = useState('');

  const supabase = createClient();

  useEffect(() => {
    fetch('/api/branding').then(r => r.json()).then(d => {
      if (d.loginBg) setBgUrl(d.loginBg);
    }).catch(() => {});
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (error) { setError(error.message); setLoading(false); }
    else { window.location.href = '/dashboard'; }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) { setError('Passwords do not match'); return; }
    setLoading(true);
    setError('');
    const { error } = await supabase.auth.signUp({ email, password, options: { emailRedirectTo: `${window.location.origin}/dashboard` } });
    if (error) { setError(error.message); setLoading(false); }
    else { setSignupSuccess(true); setLoading(false); }
  };

  const handleReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    const { error } = await supabase.auth.resetPasswordForEmail(email, { redirectTo: `${window.location.origin}/dashboard` });
    if (error) { setError(error.message); } else { setResetSent(true); }
    setLoading(false);
  };

  const inputStyle = {
    width: '100%', padding: '10px 14px', background: '#0a0a10',
    border: '1px solid #1e1e28', borderRadius: 8, color: '#e0e0e8',
    fontSize: 14, fontFamily: "'Inter', sans-serif", outline: 'none',
    boxSizing: 'border-box', transition: 'border-color 0.2s',
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', fontFamily: "'Inter', -apple-system, sans-serif", position: 'relative', overflow: 'hidden', background: '#0b0b0f' }}>
      {/* Background image  embedded base64 so it always works */}
      <div style={{
        position: 'absolute', inset: 0, zIndex: 0,
        backgroundImage: `url(${bgUrl || LOGIN_BG_FALLBACK})`,
        backgroundSize: 'cover', backgroundPosition: 'center top',
        filter: 'brightness(0.5) saturate(1.3)',
      }} />
      {/* Gradient overlay */}
      <div style={{ position: 'absolute', inset: 0, zIndex: 0, background: 'linear-gradient(180deg, rgba(11,11,15,0.15) 0%, rgba(11,11,15,0.6) 100%)' }} />

      <div style={{
        width: 380, padding: 40,
        background: 'rgba(17,17,24,0.88)', backdropFilter: 'blur(20px)', WebkitBackdropFilter: 'blur(20px)',
        borderRadius: 16, border: '1px solid rgba(255,107,74,0.15)',
        boxShadow: '0 20px 60px rgba(0,0,0,0.6)', position: 'relative', zIndex: 1,
      }}>
        <div style={{ textAlign: 'center', marginBottom: 32 }}>
          <div style={{ fontSize: 28, fontWeight: 800, color: '#ff6b4a', letterSpacing: -0.5 }}> COMMAND CENTER</div>
          <div style={{ fontSize: 11, color: '#6a6a7a', marginTop: 6, letterSpacing: 1, fontWeight: 600 }}>ADAPTIVE BY DESIGN</div>
        </div>

        {mode === 'login' ? (
          <form onSubmit={handleLogin}>
            <div style={{ marginBottom: 16 }}>
              <label style={{ fontSize: 10, fontWeight: 700, color: '#6a6a7a', letterSpacing: 0.5, display: 'block', marginBottom: 6 }}>EMAIL</label>
              <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="you@adaptivebydesign.com" required style={inputStyle}
                onFocus={e => e.target.style.borderColor = '#ff6b4a40'} onBlur={e => e.target.style.borderColor = '#1e1e28'} />
            </div>
            <div style={{ marginBottom: 8 }}>
              <label style={{ fontSize: 10, fontWeight: 700, color: '#6a6a7a', letterSpacing: 0.5, display: 'block', marginBottom: 6 }}>PASSWORD</label>
              <div style={{ position: 'relative' }}>
                <input type={showPass ? "text" : "password"} value={password} onChange={e => setPassword(e.target.value)} placeholder="" required style={{ ...inputStyle, paddingRight: 40 }}
                  onFocus={e => e.target.style.borderColor = '#ff6b4a40'} onBlur={e => e.target.style.borderColor = '#1e1e28'} />
                <button type="button" onClick={() => setShowPass(!showPass)} style={{ position: 'absolute', right: 10, top: '50%', transform: 'translateY(-50%)', background: 'none', border: 'none', color: '#6a6a7a', cursor: 'pointer', fontSize: 13, padding: 0, lineHeight: 1 }} title={showPass ? "Hide password" : "Show password"}>{showPass ? '' : ''}</button>
              </div>
            </div>
            <div style={{ textAlign: 'right', marginBottom: 20 }}>
              <span onClick={() => { setMode('reset'); setError(''); }} style={{ fontSize: 11, color: '#ff6b4a80', cursor: 'pointer' }}
                onMouseEnter={e => e.target.style.color = '#ff6b4a'} onMouseLeave={e => e.target.style.color = '#ff6b4a80'}>Forgot password?</span>
            </div>
            {error && <div style={{ padding: '8px 12px', background: '#e8545412', border: '1px solid #e8545430', borderRadius: 6, color: '#e85454', fontSize: 12, marginBottom: 16 }}>{error}</div>}
            <button type="submit" disabled={loading} style={{ width: '100%', padding: '12px 0', background: loading ? '#ff6b4a60' : '#ff6b4a', border: 'none', borderRadius: 8, color: '#fff', fontSize: 13, fontWeight: 700, letterSpacing: 0.5, cursor: loading ? 'wait' : 'pointer', fontFamily: "'Inter', sans-serif" }}>
              {loading ? 'SIGNING IN...' : 'SIGN IN'}
            </button>
            <div style={{ textAlign: 'center', marginTop: 16, fontSize: 12, color: '#6a6a7a' }}>
              Don't have an account?{' '}
              <span onClick={() => { setMode('signup'); setError(''); }} style={{ color: '#ff6b4a', cursor: 'pointer', fontWeight: 600 }}
                onMouseEnter={e => e.target.style.textDecoration = 'underline'} onMouseLeave={e => e.target.style.textDecoration = 'none'}>Sign up</span>
            </div>
          </form>
        ) : mode === 'signup' ? (
          signupSuccess ? (
            <div style={{ textAlign: 'center', padding: '20px 0' }}>
              <div style={{ fontSize: 32, marginBottom: 12 }}></div>
              <div style={{ color: '#4ecb71', fontSize: 14, fontWeight: 600, marginBottom: 8 }}>Account created!</div>
              <div style={{ color: '#6a6a7a', fontSize: 12, lineHeight: 1.5 }}>Check your email at <span style={{ color: '#e0e0e8' }}>{email}</span> to confirm your account.</div>
              <div style={{ color: '#5a5a6a', fontSize: 11, lineHeight: 1.5, marginTop: 10, padding: '10px 14px', background: '#ffffff06', borderRadius: 6, border: '1px solid #ffffff08' }}>After confirming, an admin (Billy or Clancy) will need to approve your access before you can use the Command Center.</div>
              <button type="button" onClick={() => { setMode('login'); setSignupSuccess(false); setError(''); }} style={{ marginTop: 20, padding: '10px 24px', background: '#ff6b4a', border: 'none', borderRadius: 8, color: '#fff', cursor: 'pointer', fontSize: 12, fontWeight: 600, fontFamily: "'Inter', sans-serif" }}>Back to sign in</button>
            </div>
          ) : (
            <form onSubmit={handleSignup}>
              <div style={{ marginBottom: 16 }}>
                <label style={{ fontSize: 10, fontWeight: 700, color: '#6a6a7a', letterSpacing: 0.5, display: 'block', marginBottom: 6 }}>EMAIL</label>
                <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="you@adaptivebydesign.com" required style={inputStyle}
                  onFocus={e => e.target.style.borderColor = '#ff6b4a40'} onBlur={e => e.target.style.borderColor = '#1e1e28'} />
              </div>
              <div style={{ marginBottom: 16 }}>
                <label style={{ fontSize: 10, fontWeight: 700, color: '#6a6a7a', letterSpacing: 0.5, display: 'block', marginBottom: 6 }}>PASSWORD</label>
                <div style={{ position: 'relative' }}>
                  <input type={showPass ? "text" : "password"} value={password} onChange={e => setPassword(e.target.value)} placeholder="Min 6 characters" required minLength={6} style={{ ...inputStyle, paddingRight: 40 }}
                    onFocus={e => e.target.style.borderColor = '#ff6b4a40'} onBlur={e => e.target.style.borderColor = '#1e1e28'} />
                  <button type="button" onClick={() => setShowPass(!showPass)} style={{ position: 'absolute', right: 10, top: '50%', transform: 'translateY(-50%)', background: 'none', border: 'none', color: '#6a6a7a', cursor: 'pointer', fontSize: 13, padding: 0, lineHeight: 1 }} title={showPass ? "Hide password" : "Show password"}>{showPass ? '' : ''}</button>
                </div>
              </div>
              <div style={{ marginBottom: 20 }}>
                <label style={{ fontSize: 10, fontWeight: 700, color: '#6a6a7a', letterSpacing: 0.5, display: 'block', marginBottom: 6 }}>CONFIRM PASSWORD</label>
                <input type={showPass ? "text" : "password"} value={confirmPassword} onChange={e => setConfirmPassword(e.target.value)} placeholder="Re-enter password" required minLength={6} style={inputStyle}
                  onFocus={e => e.target.style.borderColor = '#ff6b4a40'} onBlur={e => e.target.style.borderColor = '#1e1e28'} />
                {confirmPassword && password !== confirmPassword && (
                  <div style={{ fontSize: 10, color: '#e85454', marginTop: 4 }}>Passwords do not match</div>
                )}
              </div>
              {error && <div style={{ padding: '8px 12px', background: '#e8545412', border: '1px solid #e8545430', borderRadius: 6, color: '#e85454', fontSize: 12, marginBottom: 16 }}>{error}</div>}
              <button type="submit" disabled={loading} style={{ width: '100%', padding: '12px 0', background: loading ? '#ff6b4a60' : '#ff6b4a', border: 'none', borderRadius: 8, color: '#fff', fontSize: 13, fontWeight: 700, letterSpacing: 0.5, cursor: loading ? 'wait' : 'pointer', fontFamily: "'Inter', sans-serif" }}>
                {loading ? 'CREATING ACCOUNT...' : 'SIGN UP'}
              </button>
              <div style={{ textAlign: 'center', marginTop: 16, fontSize: 12, color: '#6a6a7a' }}>
                Already have an account?{' '}
                <span onClick={() => { setMode('login'); setError(''); }} style={{ color: '#ff6b4a', cursor: 'pointer', fontWeight: 600 }}
                  onMouseEnter={e => e.target.style.textDecoration = 'underline'} onMouseLeave={e => e.target.style.textDecoration = 'none'}>Sign in</span>
              </div>
            </form>
          )
        ) : (
          <form onSubmit={handleReset}>
            {resetSent ? (
              <div style={{ textAlign: 'center', padding: '20px 0' }}>
                <div style={{ fontSize: 32, marginBottom: 12 }}></div>
                <div style={{ color: '#4ecb71', fontSize: 14, fontWeight: 600, marginBottom: 8 }}>Check your email</div>
                <div style={{ color: '#6a6a7a', fontSize: 12 }}>We sent a password reset link to {email}</div>
                <button type="button" onClick={() => { setMode('login'); setResetSent(false); }} style={{ marginTop: 20, padding: '8px 20px', background: 'transparent', border: '1px solid #1e1e28', borderRadius: 6, color: '#e0e0e8', cursor: 'pointer', fontSize: 12, fontFamily: "'Inter', sans-serif" }}>Back to login</button>
              </div>
            ) : (
              <>
                <div style={{ color: '#6a6a7a', fontSize: 12, marginBottom: 16 }}>Enter your email and we'll send you a reset link.</div>
                <div style={{ marginBottom: 16 }}>
                  <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="you@adaptivebydesign.com" required style={inputStyle} />
                </div>
                {error && <div style={{ padding: '8px 12px', background: '#e8545412', border: '1px solid #e8545430', borderRadius: 6, color: '#e85454', fontSize: 12, marginBottom: 16 }}>{error}</div>}
                <button type="submit" disabled={loading} style={{ width: '100%', padding: '12px 0', background: '#ff6b4a', border: 'none', borderRadius: 8, color: '#fff', fontSize: 13, fontWeight: 700, cursor: 'pointer', fontFamily: "'Inter', sans-serif", marginBottom: 12 }}>
                  {loading ? 'SENDING...' : 'SEND RESET LINK'}
                </button>
                <button type="button" onClick={() => { setMode('login'); setError(''); }} style={{ width: '100%', padding: '10px 0', background: 'transparent', border: '1px solid #1e1e28', borderRadius: 8, color: '#6a6a7a', fontSize: 12, cursor: 'pointer', fontFamily: "'Inter', sans-serif" }}>Back to login</button>
              </>
            )}
          </form>
        )}
        <div style={{ textAlign: 'center', marginTop: 24, fontSize: 10, color: '#3a3a4a' }}> 2026 Adaptive by Design</div>
      </div>
    </div>
  );
}
